import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_BASE_URL } from '../config';

const SearchResults = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();
  const searchQuery = new URLSearchParams(location.search).get('query');

  useEffect(() => {
    // If no search query is provided, redirect to home
    if (!searchQuery || searchQuery.trim() === '') {
      navigate('/');
      return;
    }

    const fetchSearchResults = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`${API_BASE_URL}/api/videos/search?query=${encodeURIComponent(searchQuery)}`);
        setVideos(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch search results. Please try again later.');
        setLoading(false);
        console.error('Error fetching search results:', err);
      }
    };

    fetchSearchResults();
  }, [searchQuery, navigate]);

  // Format view count
  const formatViews = (views) => {
    if (views >= 1000000) {
      return `${(views / 1000000).toFixed(1)}M`;
    } else if (views >= 1000) {
      return `${(views / 1000).toFixed(1)}K`;
    }
    return views;
  };

  // Format date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) {
      return '1 day ago';
    } else if (diffDays < 30) {
      return `${diffDays} days ago`;
    } else if (diffDays < 365) {
      const months = Math.floor(diffDays / 30);
      return `${months} ${months === 1 ? 'month' : 'months'} ago`;
    } else {
      const years = Math.floor(diffDays / 365);
      return `${years} ${years === 1 ? 'year' : 'years'} ago`;
    }
  };

  // Handle image error
  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/320x180?text=Video+Thumbnail';
  };

  if (loading) {
    return <div className="container">Searching for "{searchQuery}"...</div>;
  }

  if (error) {
    return <div className="container">{error}</div>;
  }

  return (
    <div className="container">
      <h2>Search Results for "{searchQuery}"</h2>
      {videos.length === 0 ? (
        <p>No videos found matching your search.</p>
      ) : (
        <div className="video-grid">
          {videos.map((video) => (
            <Link to={`/video/${video._id}`} key={video._id} className="video-card">
              <img 
                src={`${API_BASE_URL}${video.thumbnailPath}`} 
                alt={video.title} 
                className="video-thumbnail"
                onError={handleImageError}
              />
              <div className="video-info">
                <h3 className="video-title">{video.title}</h3>
                <p>{video.uploader}</p>
                <div className="video-meta">
                  <span>{formatViews(video.views)} views</span>
                  <span>{formatDate(video.createdAt)}</span>
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchResults;
