import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_BASE_URL } from '../config';
import { AuthContext } from '../context/AuthContext';
import '../styles/Channel.css';

const Channel = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentUser } = useContext(AuthContext);

  const [channel, setChannel] = useState(null);
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [subscriberCount, setSubscriberCount] = useState(0);
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const fetchChannelData = async () => {
      try {
        // Fetch channel info
        const [channelResponse, videosResponse, subscriberCountResponse] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/users/${id}`),
          axios.get(`${API_BASE_URL}/api/videos/channel/${id}`),
          axios.get(`${API_BASE_URL}/api/subscriptions/count/${id}`)
        ]);

        setChannel(channelResponse.data);
        setVideos(videosResponse.data);
        setSubscriberCount(subscriberCountResponse.data.count);

        // Get subscription status if logged in
        if (currentUser) {
          const statusResponse = await axios.get(
            `${API_BASE_URL}/api/subscriptions/status/${id}`,
            {
              headers: { Authorization: `Bearer ${currentUser.token}` }
            }
          );
          setIsSubscribed(statusResponse.data.isSubscribed);
        }

        setLoading(false);
      } catch (err) {
        setError('Failed to load channel data');
        setLoading(false);
        console.error('Error fetching channel data:', err);
      }
    };

    fetchChannelData();
  }, [id, currentUser]);

  const handleSubscribe = async () => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    try {
      setIsSubmitting(true);
      const response = await axios.post(
        `${API_BASE_URL}/api/subscriptions/${id}`,
        {},
        {
          headers: { Authorization: `Bearer ${currentUser.token}` }
        }
      );

      setIsSubscribed(response.data.isSubscribed);
      setSubscriberCount(prev => response.data.isSubscribed ? prev + 1 : prev - 1);
    } catch (err) {
      console.error('Error updating subscription:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  const formatViews = (views) => {
    if (views >= 1000000) {
      return `${(views / 1000000).toFixed(1)}M`;
    } else if (views >= 1000) {
      return `${(views / 1000).toFixed(1)}K`;
    }
    return views;
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return <div className="container">Loading channel...</div>;
  }

  if (error) {
    return <div className="container">{error}</div>;
  }

  if (!channel) {
    return <div className="container">Channel not found</div>;
  }

  return (
    <div className="container">
      <div className="channel-header">
        <div className="channel-info">
          <img
            src={`${API_BASE_URL}${channel.profilePicture}`}
            alt={channel.username}
            className="channel-avatar"
            onError={(e) => {
              e.target.src = `${API_BASE_URL}/default-avatar.png`;
            }}
          />
          <div className="channel-details">
            <h1>{channel.username}</h1>
            <p className="subscriber-count">{subscriberCount} {subscriberCount === 1 ? 'subscriber' : 'subscribers'}</p>
            {channel.bio && <p className="channel-bio">{channel.bio}</p>}
          </div>
          {currentUser && currentUser._id !== id && (
            <button
              className={`subscribe-button ${isSubscribed ? 'subscribed' : ''}`}
              onClick={handleSubscribe}
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Processing...' : isSubscribed ? 'Subscribed' : 'Subscribe'}
            </button>
          )}
        </div>
      </div>

      <div className="channel-videos">
        <h2>Videos</h2>
        {videos.length === 0 ? (
          <p>No videos uploaded yet.</p>
        ) : (
          <div className="video-grid">
            {videos.map((video) => (
              <Link to={`/video/${video._id}`} key={video._id} className="video-card">
                <img
                  src={`${API_BASE_URL}${video.thumbnailPath}`}
                  alt={video.title}
                  className="video-thumbnail"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/320x180?text=Video+Thumbnail';
                  }}
                />
                <div className="video-info">
                  <h3 className="video-title">{video.title}</h3>
                  <div className="video-meta">
                    <span>{formatViews(video.views)} views</span>
                    <span>{formatDate(video.createdAt)}</span>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Channel;
