import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_BASE_URL } from '../config';
import { AuthContext } from '../context/AuthContext';
import { ThumbUpAlt, ThumbDownAlt } from '@mui/icons-material';
import CommentSection from './CommentSection';
import { Box, IconButton, Typography } from '@mui/material';

const VideoPlayer = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentUser } = useContext(AuthContext);

  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);
  const [liked, setLiked] = useState(false);
  const [disliked, setDisliked] = useState(false);
  const [videoError, setVideoError] = useState(false);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/videos/${id}`);
        setVideo(response.data);
        setLikes(response.data.likes);
        setDislikes(response.data.dislikes);

        // Check if user has liked this video
        if (currentUser && response.data.likedBy) {
          setLiked(response.data.likedBy.includes(currentUser._id));
        }

        setLoading(false);
      } catch (err) {
        if (err.response && err.response.status === 404) {
          navigate('/');
          return;
        }
        setError('Failed to load video. Please try again later.');
        setLoading(false);
        console.error('Error fetching video:', err);
      }
    };

    fetchVideo();
  }, [id, navigate, currentUser]);

  const handleLike = async () => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    try {
      const response = await axios.post(
        `${API_BASE_URL}/api/videos/${id}/like`,
        {},
        {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        }
      );
      setLikes(response.data.likes);
      setLiked(response.data.liked);
    } catch (err) {
      console.error('Error liking video:', err);
    }
  };

  const handleDislike = async () => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    try {
      const response = await axios.post(
        `${API_BASE_URL}/api/videos/${id}/dislike`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }}
      );

      setLikes(response.data.likes);
      setDislikes(response.data.dislikes);
      setLiked(response.data.liked);
      setDisliked(response.data.disliked);
    } catch (error) {
      console.error('Error disliking video:', error);
    }
  };

  // Format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Handle video error
  const handleVideoError = () => {
    setVideoError(true);
  };

  // Handle poster image error
  const handlePosterError = (e) => {
    e.target.src = 'https://via.placeholder.com/1280x720?text=Video+Unavailable';
  };

  if (loading) {
    return <div className="container">Loading video...</div>;
  }

  if (error) {
    return <div className="container">{error}</div>;
  }

  if (!video) {
    return <div className="container">Video not found</div>;
  }

  return (
    <div className="container">
      <div className="video-player-container">
        {videoError ? (
          <div className="video-error">
            <img 
              src="https://via.placeholder.com/1280x720?text=Video+Unavailable" 
              alt="Video unavailable" 
              className="video-player"
            />
            <p className="error-message">This video is unavailable. It may have been removed or is temporarily inaccessible.</p>
          </div>
        ) : (
          <video 
            className="video-player" 
            src={`${API_BASE_URL}${video.filepath}`} 
            controls 
            autoPlay
            poster={`${API_BASE_URL}${video.thumbnailPath}`}
            onError={handleVideoError}
            onPosterError={handlePosterError}
          ></video>
        )}

        <div className="video-details">
          <h1>{video.title}</h1>
          <div className="video-stats">
            <span>{video.views} views â€¢ {formatDate(video.createdAt)}</span>
          </div>

          <div className="video-actions">
            <IconButton onClick={handleLike} color={liked ? 'primary' : 'default'}>
              <ThumbUpAlt />
            </IconButton>
            <Typography variant="body2" sx={{ mr: 2 }}>
              {likes}
            </Typography>

            <IconButton onClick={handleDislike} color={disliked ? 'primary' : 'default'}>
              <ThumbDownAlt />
            </IconButton>
            <Typography variant="body2" sx={{ mr: 2 }}>
              {dislikes}
            </Typography>
          </div>

          <div className="uploader-info">
            <h3>{video.uploader}</h3>
          </div>

          <div className="video-description">
            <p>{video.description}</p>
          </div>
        </div>
      </div>

      <CommentSection videoId={video._id} />
    </div>
  );
};

export default VideoPlayer;
