import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_BASE_URL } from '../config';
import { AuthContext } from '../context/AuthContext';

const DeviceVerification = () => {
  const [verificationCode, setVerificationCode] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { currentUser, login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const deviceId = localStorage.getItem('deviceId');

      const response = await axios.post(`${API_BASE_URL}/api/device-verification/verify`, {
        userId: currentUser._id,
        deviceId,
        code: verificationCode
      }, {
        headers: {
          'Authorization': `Bearer ${currentUser.token}`
        }
      });

      // Store the device verification status
      localStorage.setItem('isDeviceVerified', 'true');

      // Navigate to home page
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to verify code. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleResendCode = async () => {
    setError('');
    setIsLoading(true);

    try {
      await axios.post(`${API_BASE_URL}/api/device-verification/request`, {
        userId: currentUser._id,
        email: currentUser.email
      }, {
        headers: {
          'Authorization': `Bearer ${currentUser.token}`
        }
      });

      setError('A new verification code has been sent to your email.');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to send verification code. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form-container">
        <h2>Device Verification Required</h2>
        <p>
          We've sent a verification code to your email address.
          Please enter the 6-digit code below to verify this device.
        </p>

        {error && <div className="alert alert-danger">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter 6-digit code"
              value={verificationCode}
              onChange={(e) => setVerificationCode(e.target.value.replace(/\D/g, '').slice(0, 6))}
              required
              pattern="[0-9]{6}"
              maxLength="6"
            />
          </div>

          <button 
            type="submit" 
            className="btn btn-primary btn-block"
            disabled={isLoading || verificationCode.length !== 6}
          >
            {isLoading ? 'Verifying...' : 'Verify Device'}
          </button>
        </form>

        <div className="mt-3 text-center">
          <button 
            onClick={handleResendCode}
            className="btn btn-link"
            disabled={isLoading}
          >
            Resend Verification Code
          </button>
        </div>
      </div>
    </div>
  );
};

export default DeviceVerification;
