import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { API_BASE_URL } from '../config';
import { AuthContext } from '../context/AuthContext';

const EditVideoModal = ({ video, onClose, onUpdate }) => {
  const { currentUser, checkVerificationStatus } = useContext(AuthContext);
  const [title, setTitle] = useState(video.title);
  const [description, setDescription] = useState(video.description);
  const [thumbnail, setThumbnail] = useState(null);
  const [thumbnailPreview, setThumbnailPreview] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [isCheckingVerification, setIsCheckingVerification] = useState(false);

  useEffect(() => {
    const verifyStatus = async () => {
      setIsCheckingVerification(true);
      try {
        await checkVerificationStatus();
      } catch (err) {
        console.error('Error checking verification:', err);
      } finally {
        setIsCheckingVerification(false);
      }
    };
    verifyStatus();
  }, []);

  const handleThumbnailChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setThumbnail(file);
      setThumbnailPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      // Check verification status again before submitting
      await checkVerificationStatus();

      if (!currentUser?.isVerified) {
        setError('Please verify your email before editing videos. If you have already verified your email, try logging out and logging back in.');
        return;
      }

      const formData = new FormData();
      formData.append('title', title);
      formData.append('description', description);
      if (thumbnail) {
        formData.append('thumbnail', thumbnail);
      }

      const response = await axios.put(
        `${API_BASE_URL}/api/videos/${video._id}`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${currentUser.token}`
          }
        }
      );

      onUpdate(response.data);
      onClose();
    } catch (err) {
      console.error('Error updating video:', err);
      if (err.response?.status === 401) {
        if (err.response?.data?.message?.includes('verify')) {
          setError('Please verify your email before editing videos. If you have already verified your email, try logging out and logging back in.');
        } else {
          setError('Your session has expired. Please log in again.');
        }
      } else {
        setError(err.response?.data?.message || 'Failed to update video');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Edit Video</h2>
        {error && (
          <div className="alert alert-danger">
            {error}
            {error.includes('verify') && (
              <button 
                className="btn btn-link"
                onClick={checkVerificationStatus}
                disabled={isCheckingVerification}
              >
                {isCheckingVerification ? 'Checking...' : 'Check verification status'}
              </button>
            )}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows="4"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="thumbnail">Thumbnail</label>
            <input
              type="file"
              id="thumbnail"
              className="form-control"
              accept="image/*"
              onChange={handleThumbnailChange}
            />
            {thumbnailPreview && (
              <img
                src={thumbnailPreview}
                alt="Thumbnail preview"
                className="thumbnail-preview"
              />
            )}
          </div>

          <div className="modal-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={onClose}
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditVideoModal;
