import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';
import { API_BASE_URL } from '../config';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [tempUserData, setTempUserData] = useState(null);

  useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) {
      setCurrentUser(JSON.parse(user));
    }
    setLoading(false);
  }, []);

  const login = async (email, password) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/users/login`, {
        email,
        password
      });

      const user = response.data;

      // Store user data temporarily
      setTempUserData(user);

      // Request verification code
      await axios.post(`${API_BASE_URL}/api/device-verification/request`, {
        userId: user._id,
        email: user.email
      });

      return { requiresVerification: true };
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to login');
      throw err;
    }
  };

  const verifyLoginCode = async (code) => {
    try {
      if (!tempUserData) {
        throw new Error('No login attempt in progress');
      }

      const response = await axios.post(`${API_BASE_URL}/api/device-verification/verify`, {
        userId: tempUserData._id,
        code: code
      });

      if (response.data.verified) {
        // Set the user as logged in
        setCurrentUser(tempUserData);
        localStorage.setItem('user', JSON.stringify(tempUserData));
        setTempUserData(null);
        return true;
      }
      return false;
    } catch (err) {
      throw err;
    }
  };

  const logout = () => {
    setCurrentUser(null);
    setTempUserData(null);
    localStorage.removeItem('user');
  };

  const value = {
    currentUser,
    loading,
    error,
    login,
    logout,
    verifyLoginCode,
    setError
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
