import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import { API_BASE_URL } from '../config';
import { formatDistanceToNow } from 'date-fns';
import { ThumbUpAlt, ThumbDownAlt, Reply, Delete, Edit } from '@mui/icons-material';
import { IconButton, TextField, Button, Avatar, Box, Typography } from '@mui/material';

const Comment = ({ comment, videoId, onDelete, onUpdate }) => {
  const { currentUser } = useContext(AuthContext);
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState(comment.content);
  const [showReplyForm, setShowReplyForm] = useState(false);
  const [replyContent, setReplyContent] = useState('');
  const [likes, setLikes] = useState(comment.likes);
  const [dislikes, setDislikes] = useState(comment.dislikes);
  const [liked, setLiked] = useState(comment.likedBy?.includes(currentUser?._id));
  const [disliked, setDisliked] = useState(comment.dislikedBy?.includes(currentUser?._id));

  const handleLike = async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/videos/comments/${comment._id}/like`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setLikes(response.data.likes);
      setDislikes(response.data.dislikes);
      setLiked(response.data.liked);
      setDisliked(response.data.disliked);
    } catch (error) {
      console.error('Error liking comment:', error);
    }
  };

  const handleDislike = async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/videos/comments/${comment._id}/dislike`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setLikes(response.data.likes);
      setDislikes(response.data.dislikes);
      setLiked(response.data.liked);
      setDisliked(response.data.disliked);
    } catch (error) {
      console.error('Error disliking comment:', error);
    }
  };

  const handleEdit = async () => {
    try {
      await axios.put(`${API_BASE_URL}/api/videos/comments/${comment._id}`, 
        { content: editedContent },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }}
      );

      onUpdate({ ...comment, content: editedContent });
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating comment:', error);
    }
  };

  const handleReply = async () => {
    try {
      const response = await axios.post(
        `${API_BASE_URL}/api/videos/${videoId}/comments`,
        {
          content: replyContent,
          parentCommentId: comment._id
        },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }}
      );

      // Add the new reply to the parent comment's replies
      onUpdate({
        ...comment,
        replies: [...(comment.replies || []), response.data]
      });

      setShowReplyForm(false);
      setReplyContent('');
    } catch (error) {
      console.error('Error adding reply:', error);
    }
  };

  return (
    <Box sx={{ mb: 2 }}>
      <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 1 }}>
        <Avatar
          src={comment.user.profilePicture}
          alt={comment.user.username}
          sx={{ mr: 2, width: 40, height: 40 }}
        />
        <Box sx={{ flexGrow: 1 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 0.5 }}>
            <Typography variant="subtitle2" sx={{ mr: 1 }}>
              {comment.user.username}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {formatDistanceToNow(new Date(comment.createdAt), { addSuffix: true })}
            </Typography>
          </Box>

          {isEditing ? (
            <Box sx={{ mb: 1 }}>
              <TextField
                fullWidth
                multiline
                value={editedContent}
                onChange={(e) => setEditedContent(e.target.value)}
                size="small"
                sx={{ mb: 1 }}
              />
              <Button variant="contained" size="small" onClick={handleEdit} sx={{ mr: 1 }}>
                Save
              </Button>
              <Button size="small" onClick={() => setIsEditing(false)}>
                Cancel
              </Button>
            </Box>
          ) : (
            <Typography variant="body2" sx={{ mb: 1 }}>
              {comment.content}
            </Typography>
          )}

          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <IconButton size="small" onClick={handleLike} color={liked ? 'primary' : 'default'}>
              <ThumbUpAlt fontSize="small" />
            </IconButton>
            <Typography variant="caption" sx={{ mx: 0.5 }}>
              {likes}
            </Typography>

            <IconButton size="small" onClick={handleDislike} color={disliked ? 'primary' : 'default'}>
              <ThumbDownAlt fontSize="small" />
            </IconButton>
            <Typography variant="caption" sx={{ mx: 0.5 }}>
              {dislikes}
            </Typography>

            <Button
              size="small"
              startIcon={<Reply />}
              onClick={() => setShowReplyForm(!showReplyForm)}
              sx={{ ml: 1 }}
            >
              Reply
            </Button>

            {currentUser?._id === comment.user._id && (
              <>
                <IconButton size="small" onClick={() => setIsEditing(true)} sx={{ ml: 1 }}>
                  <Edit fontSize="small" />
                </IconButton>
                <IconButton size="small" onClick={() => onDelete(comment._id)} sx={{ ml: 1 }}>
                  <Delete fontSize="small" />
                </IconButton>
              </>
            )}
          </Box>

          {showReplyForm && (
            <Box sx={{ mt: 2 }}>
              <TextField
                fullWidth
                multiline
                placeholder="Write a reply..."
                value={replyContent}
                onChange={(e) => setReplyContent(e.target.value)}
                size="small"
                sx={{ mb: 1 }}
              />
              <Button variant="contained" size="small" onClick={handleReply} sx={{ mr: 1 }}>
                Reply
              </Button>
              <Button size="small" onClick={() => setShowReplyForm(false)}>
                Cancel
              </Button>
            </Box>
          )}

          {comment.replies?.length > 0 && (
            <Box sx={{ ml: 4, mt: 2 }}>
              {comment.replies.map((reply) => (
                <Comment
                  key={reply._id}
                  comment={reply}
                  videoId={videoId}
                  onDelete={onDelete}
                  onUpdate={onUpdate}
                />
              ))}
            </Box>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default Comment;
