import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { API_BASE_URL } from '../config';

const VideoPlayer = () => {
  const { id } = useParams();
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [likes, setLikes] = useState(0);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/videos/${id}`);
        setVideo(response.data);
        setLikes(response.data.likes);
        setLoading(false);
      } catch (err) {
        setError('Failed to load video. Please try again later.');
        setLoading(false);
        console.error('Error fetching video:', err);
      }
    };

    fetchVideo();
  }, [id]);

  const handleLike = async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/videos/${id}/like`);
      setLikes(response.data.likes);
    } catch (err) {
      console.error('Error liking video:', err);
    }
  };

  // Format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return <div className="container">Loading video...</div>;
  }

  if (error) {
    return <div className="container">{error}</div>;
  }

  if (!video) {
    return <div className="container">Video not found</div>;
  }

  return (
    <div className="container">
      <div className="video-player-container">
        <video 
          className="video-player" 
          src={`${API_BASE_URL}${video.filepath}`} 
          controls 
          autoPlay
        ></video>

        <div className="video-details">
          <h1>{video.title}</h1>
          <div className="video-stats">
            <span>{video.views} views â€¢ {formatDate(video.createdAt)}</span>
          </div>

          <div className="video-actions">
            <button className="action-button" onClick={handleLike}>
              <i className="fas fa-thumbs-up"></i> {likes} Likes
            </button>
          </div>

          <div className="uploader-info">
            <h3>{video.uploader}</h3>
          </div>

          <div className="video-description">
            <p>{video.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
