import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { API_BASE_URL } from '../config';

const VideoUpload = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [videoFile, setVideoFile] = useState(null);
  const [thumbnailFile, setThumbnailFile] = useState(null);
  const [thumbnailPreview, setThumbnailPreview] = useState(null);
  const [uploader, setUploader] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [progress, setProgress] = useState(0);

  const navigate = useNavigate();

  const handleVideoChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type.includes('video/')) {
      setVideoFile(selectedFile);
      setError(null);
    } else {
      setVideoFile(null);
      setError('Please select a valid video file');
    }
  };

  const handleThumbnailChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type.includes('image/')) {
      setThumbnailFile(selectedFile);

      // Create a preview URL for the thumbnail
      const previewUrl = URL.createObjectURL(selectedFile);
      setThumbnailPreview(previewUrl);

      setError(null);
    } else {
      setThumbnailFile(null);
      setThumbnailPreview(null);
      setError('Please select a valid image file for the thumbnail');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!videoFile) {
      setError('Please select a video file to upload');
      return;
    }

    if (!title.trim()) {
      setError('Please enter a title for your video');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('video', videoFile);
    formData.append('title', title);
    formData.append('description', description);
    formData.append('uploader', uploader || 'Anonymous');

    if (thumbnailFile) {
      formData.append('thumbnail', thumbnailFile);
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/api/videos`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percentCompleted);
        }
      });

      setLoading(false);
      navigate(`/video/${response.data._id}`);
    } catch (err) {
      setLoading(false);
      setError('Failed to upload video. Please try again.');
      console.error('Error uploading video:', err);
    }
  };

  return (
    <div className="container">
      <h2>Upload Video</h2>
      <form className="upload-form" onSubmit={handleSubmit}>
        {error && <div className="alert alert-danger">{error}</div>}

        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            className="form-control"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter video title"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            className="form-control"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter video description"
            rows="4"
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="uploader">Your Name</label>
          <input
            type="text"
            id="uploader"
            className="form-control"
            value={uploader}
            onChange={(e) => setUploader(e.target.value)}
            placeholder="Enter your name (optional)"
          />
        </div>

        <div className="form-group">
          <label htmlFor="video">Video File</label>
          <input
            type="file"
            id="video"
            className="form-control"
            accept="video/*"
            onChange={handleVideoChange}
            required
          />
          <small>Max file size: 100MB</small>
        </div>

        <div className="form-group">
          <label htmlFor="thumbnail">Thumbnail Image (Optional)</label>
          <input
            type="file"
            id="thumbnail"
            className="form-control"
            accept="image/*"
            onChange={handleThumbnailChange}
          />
          <small>Recommended size: 1280x720 pixels. Max file size: 5MB</small>

          {thumbnailPreview && (
            <div className="thumbnail-preview">
              <img 
                src={thumbnailPreview} 
                alt="Thumbnail preview" 
                style={{ maxWidth: '100%', maxHeight: '200px', marginTop: '10px' }}
              />
            </div>
          )}
        </div>

        {loading && (
          <div className="progress">
            <div 
              className="progress-bar" 
              role="progressbar" 
              style={{ width: `${progress}%` }}
              aria-valuenow={progress} 
              aria-valuemin="0" 
              aria-valuemax="100"
            >
              {progress}%
            </div>
          </div>
        )}

        <button 
          type="submit" 
          className="btn btn-primary" 
          disabled={loading}
        >
          {loading ? 'Uploading...' : 'Upload Video'}
        </button>
      </form>
    </div>
  );
};

export default VideoUpload;
