import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { API_BASE_URL } from '../config';

const Home = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/videos`);
        setVideos(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch videos. Please try again later.');
        setLoading(false);
        console.error('Error fetching videos:', err);
      }
    };

    fetchVideos();
  }, []);

  // Format view count
  const formatViews = (views) => {
    if (views >= 1000000) {
      return `${(views / 1000000).toFixed(1)}M`;
    } else if (views >= 1000) {
      return `${(views / 1000).toFixed(1)}K`;
    }
    return views;
  };

  // Format date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) {
      return '1 day ago';
    } else if (diffDays < 30) {
      return `${diffDays} days ago`;
    } else if (diffDays < 365) {
      const months = Math.floor(diffDays / 30);
      return `${months} ${months === 1 ? 'month' : 'months'} ago`;
    } else {
      const years = Math.floor(diffDays / 365);
      return `${years} ${years === 1 ? 'year' : 'years'} ago`;
    }
  };

  // Handle image error
  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/320x180?text=Video+Thumbnail';
  };

  if (loading) {
    return <div className="container">Loading videos...</div>;
  }

  if (error) {
    return <div className="container">{error}</div>;
  }

  return (
    <div className="container">
      <h2>Recommended Videos</h2>
      {videos.length === 0 ? (
        <p>No videos available. Be the first to upload!</p>
      ) : (
        <div className="video-grid">
          {videos.map((video) => (
            <Link to={`/video/${video._id}`} key={video._id} className="video-card">
              <img 
                src={`${API_BASE_URL}${video.thumbnailPath}`} 
                alt={video.title} 
                className="video-thumbnail"
                onError={handleImageError}
              />
              <div className="video-info">
                <h3 className="video-title">{video.title}</h3>
                <div className="video-uploader">
                  <img 
                    src={`${API_BASE_URL}${video.uploader.profilePicture}`}
                    alt={video.uploader.username}
                    className="uploader-thumbnail"
                    onError={(e) => {
                      e.target.src = `${API_BASE_URL}/uploads/profiles/default-profile.svg`;
                    }}
                  />
                  <span>{video.uploader.username}</span>
                </div>
                <div className="video-meta">
                  <span>{formatViews(video.views)} views</span>
                  <span>{formatDate(video.createdAt)}</span>
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
